mapper.select:
SELECT *
FROM ORDER_ITEM O, SUP S, ITEM_TYPE T, ITEM SP, DELIVER D
WHERE O.ITEM_NUM = SP.ITEM_NUM
AND SP.SUP_NUM = S.SUP_NUM
AND SP.TYPE_NUM = T.TYPE_NUM
AND O.DELI_NUM = D.DELI_NUM
AND MONTH(O.ORDER_DATE) = #{currentMonth}
AND YEAR(O.ORDER_DATE) = #{currentYear}

    AND ${searchType} LIKE CONCAT('%', #{searchValue}, '%')

ORDER BY O.ORDER_DATE DESC;

SELECT * FROM ORDER_ITEM O, SUP S, ITEM_TYPE T, ITEM SP, DELIVER D WHERE O.ITEM_NUM = SP.ITEM_NUM AND SP.SUP_NUM = S.SUP_NUM AND SP.TYPE_NUM = T.TYPE_NUM AND O.DELI_NUM = D.DELI_NUM AND O.ORDER_DATE >= DATE_FORMAT(DATE_SUB(CURRENT_DATE(), INTERVAL 1 MONTH), '%Y-%m-01') AND O.ORDER_DATE < DATE_FORMAT(CURRENT_DATE(), '%Y-%m-01')
AND ${searchType} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY O.ORDER_DATE DESC;
