mapper.select:
SELECT * FROM ORDER_ITEM O, DELIVER D, ITEM SP, SUP S, ITEM_TYPE T, ORDER_DETAIL OD WHERE O.DELI_NUM = D.DELI_NUM AND OD.ITEM_NUM = SP.ITEM_NUM AND SP.SUP_NUM = S.SUP_NUM AND SP.TYPE_NUM = T.TYPE_NUM AND O.ORDER_NUM = OD.ORDER_NUM AND MONTH(O.ORDER_DATE) = #{currentMonth} AND YEAR(O.ORDER_DATE) = #{currentYear} AND CUS_NUM =1
AND ${searchType} LIKE CONCAT('%', #{searchValue}, '%')
ORDER BY O.ORDER_DATE DESC;

mapper.update:
UPDATE ORDER_ITEM
SET DELI_NUM = 5
WHERE ORDER_NUM = #{orderNum};

mapper.select:
SELECT T.TYPE_NAME, I.ITEM_NAME, SUM(OD.ORDER_CNT) AS STOCK_ITEM_CNT, S.SUP_NAME FROM ORDER_ITEM O, ITEM I, SUP S, ITEM_TYPE T, ORDER_DETAIL OD, deliver D WHERE O.DELI_NUM = D.DELI_NUM AND D.DELI_NUM = 4 AND OD.ITEM_NUM = I.ITEM_NUM AND I.SUP_NUM = S.SUP_NUM AND I.TYPE_NUM = T.TYPE_NUM AND O.ORDER_NUM = OD.ORDER_NUM AND O.CUS_NUM =1
AND ${searchType} LIKE CONCAT('%', #{searchValue}, '%')
GROUP BY T.TYPE_NAME, I.ITEM_NAME, S.SUP_NAME ORDER BY O.ORDER_DATE DESC;
