mapper.select:
SELECT STAFF_ID, STAFF_PW, STAFF_ROLE, STAFF_NAME, STAFF_NUM
FROM HOS_STAFF
WHERE STAFF_ID=#{staffId}
AND STAFF_PW=#{staffPw}

mapper.insert:
INSERT INTO HOS_STAFF (
    PART_NUM
    , STAFF_NAME
    , STAFF_ID
    , STAFF_PW
    , STAFF_BIRTH
    , STAFF_ADDR
    , STAFF_TEL
    , STAFF_GEN
) VALUES (
    #{partNum}
    , #{staffName}
    , #{staffId}
    , #{staffPw}
    , #{staffBirth}
    , #{staffAddr}
    , #{staffTel}
    , #{staffGen}
);

mapper.update:
UPDATE HOS_STAFF
SET
    STAFF_TEL = #{staffTel}
    , STAFF_ADDR = #{staffAddr}
    , STAFF_ID = #{staffId}
    , STAFF_PW = #{staffPw}
    , HIRE_DATE = #{hireDate}
WHERE STAFF_NUM = #{staffNum}
